let cards = document.getElementsByClassName("card-name");
let colors = ["#ec407a","#ab47bc","#ef5350","#7e57c2","#5c6bc0","#ffa726"];
const random = (min,max)=> Math.floor(Math.random() * (max - min) + min) 
for(let c of cards){
    c.children[1].style.background = colors[random(0,colors.length)];
    c.children[1].children[0].innerHTML = c.children[0].innerHTML.substring(0,2)
}